INSERT INTO `pn_lenses` VALUES (441, 'Nike Maxsight', 'Nike Maxsite', 4, 11, 1, 0, 0.06, 8.4, NULL, 'Cast-Mold', 'molded', 'varies', 30, 'Monthly replacement', 'daily wear', '0.00', '', 'http://www.bausch.com/en_US/package_insert/visioncare/softcontacts/nike_pkg_insert.pdf', 'http://www.bausch.com/en_US/ecp/visioncare/product/softcontacts/nike_maxsight_ecp.aspx', '', '<p> "Nike MAXSIGHT is available in two glare-reducing tints. Its patented Light Architecture selectively filters specific wavelengths of light within the visual spectrum to enhance key elements in sport."</p>\r\nThe grey-green tint is designed for golf, running, overall training, and other sports played in bright sunlight where visual comfort and glare is a concern. Grey-green enhances the green and red portions of the visual spectrum to improve detail and contour recognition.\r\n\r\nThe amber tint is designed for fast-moving sports like soccer, tennis, baseball, and footballwhen tracking a fast-moving ball against different backgrounds is important. <p>Amber selectively filters specific wavelengths in the blue-green portion of the visible spectrum, making the ball pop off the background.</p>', 0, '1', '', 'med', 4.00, -9.00, 14.30, 14.30, 14.30, '8.7', '+4.00 to -6.50 (-9.00)', NULL, '', '', NULL, '', '', '', 0, '', '', '', NULL, '', NULL, NULL, '', 0, '', '', NULL, 0, '', NULL, '', NULL, '2005-09-06');
